import React, { useEffect, useState } from 'react';
import { Button, Container, Form, Table } from 'react-bootstrap';
import { searchTask } from '../../api/Manager';
import HeaderComponent from '../../components/Header';

const EmployeeInf = () => {
    const [originalTasks, setOriginalTasks] = useState([]); // Store original tasks
    const [filteredTasks, setFilteredTasks] = useState([]); // Store filtered tasks
    const [query, setQuery] = useState("");
    const [searchSystem, setSearchSystem] = useState("");
    const [searchPL, setSearchPL] = useState("");
    const [searchModel, setSearchModel] = useState("");
    const [searchType, setSearchType] = useState("");
    const [searchCapacity, setSearchCapacity] = useState("");

    const getData = async () => {
        return await searchTask(query)
            .then(res => {
                setOriginalTasks(res.data); // Store original tasks
                setFilteredTasks(res.data); // Initialize filtered tasks
            }).catch(err => console.log(err));
    };

    useEffect(() => {
        getData();
    }, [query]);

    const handleChange = (e) => {
        setQuery(e.target.value);
    };

    const filterTasks = (tasks) => {
        setFilteredTasks(tasks);
    };

    const applyFilters = () => {
        let tempTasks = [...originalTasks];
        if (searchSystem) {
            tempTasks = tempTasks.filter(task =>
                task.system && (task.system.toLowerCase() === searchSystem.toLowerCase() || task.system.toLowerCase().startsWith(searchSystem.toLowerCase()))
            );
        }
        if (searchPL) {
            tempTasks = tempTasks.filter(task =>
                task.productline && (task.productline.toLowerCase() === searchPL.toLowerCase() || task.productline.toLowerCase().includes(searchPL.toLowerCase()))
            );
        }
        if (searchModel) {
            tempTasks = tempTasks.filter(task =>
                task.model && (task.model.toLowerCase() === searchModel.toLowerCase() || task.model.toLowerCase().startsWith(searchModel.toLowerCase()))
            );
        }
        if (searchType) {
            tempTasks = tempTasks.filter(task =>
                task.type && (task.type.toLowerCase() === searchType.toLowerCase() || task.type.toLowerCase().startsWith(searchType.toLowerCase()))
            );
        }
        if (searchCapacity) {
            const inputValue = parseFloat(searchCapacity);
            if (!isNaN(inputValue)) {
                tempTasks = tempTasks.filter(task => task.capacity > inputValue);
            }
        }
        filterTasks(tempTasks);
    };

    const handleSearchSystem = (e) => {
        const input = e.target.value;
        setSearchSystem(input); // Update state for the system input
        applyFilters(); // Apply filters on change
    };

    const handleSearchPL = (e) => {
        const input = e.target.value;
        setSearchPL(input); // Update state for the product line input
        applyFilters(); // Apply filters on change
    };

    const handleSearchModel = (e) => {
        const input = e.target.value;
        setSearchModel(input); // Update state for the model input
        applyFilters(); // Apply filters on change
    };

    const handleSearchType = (e) => {
        const input = e.target.value;
        setSearchType(input); // Update state for the type input
        applyFilters(); // Apply filters on change
    };

    const handleSearchCapacity = (e) => {
        const input = e.target.value;
        setSearchCapacity(input); // Update state for the capacity input
        applyFilters(); // Apply filters on change
    };

    const handleClearFilter = () => {
        setFilteredTasks(originalTasks); // Reset to original tasks
        setQuery(""); // Clear query input
        setSearchSystem(""); // Clear system input
        setSearchPL(""); // Clear product line input
        setSearchModel(""); // Clear model input
        setSearchType(""); // Clear type input
        setSearchCapacity(""); // Clear capacity input
    };

    const row = filteredTasks.map((task, index) => (
        <tr key={index}>
            <td>{task.system}</td>
            <td>{task.productline}</td>
            <td>{task.model}</td>
            <td>{task.type}</td>
            <td>{task.capacity}</td>
            {/* Add other task properties as needed */}
        </tr>
    ));

    return (
        <>
            <HeaderComponent />
            <Container style={{ alignItems: 'center', marginLeft: 100 }}>
                <h1 style={{ marginLeft: 300 }} className='text-center'>Panasonic Devices</h1>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={query} 
                        onChange={handleChange} 
                    />
                </Form>

                <h3>System</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchSystem} 
                        onChange={handleSearchSystem} 
                    />
                </Form>

                <h3>Product Line</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchPL} 
                        onChange={handleSearchPL} 
                    />
                </Form>

                <h3>Model</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchModel} 
                        onChange={handleSearchModel} 
                    />
                </Form>

                <h3>Type</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchType} 
                        onChange={handleSearchType} 
                    />
                </Form>

                <h3>Capacity</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='number' 
                        value={searchCapacity} 
                        onChange={handleSearchCapacity} 
                    />
                </Form>

                <Button onClick={handleClearFilter}>Clear Filter</Button>
                <Table>
                    <thead>
                        <tr>
                            <th>System</th>
                            <th>Product Line</th>
                            <th>Model</th>
                            <th>Type</th>
                            <th>Capacity</th>
                            {/* Add other headers as needed */}
                        </tr>
                    </thead>
                    <tbody>
                        {row}
                    </tbody>
                </Table>
            </Container>
        </>
    );
};

export default EmployeeInf;
