import React, { useEffect, useState } from 'react';
import { Button, Container, Form, Table } from 'react-bootstrap';
import { searchTask } from '../../api/Manager';
import HeaderComponent from '../../components/Header';

const EmployeeInf = () => {
    const [tasks, setTasks] = useState([]);
    const [query, setQuery] = useState("");
    const [system, setSystem] = useState("");
    const [productLine, setProductLine] = useState("");
    const [model, setModel] = useState("");
    const [type, setType] = useState("");
    const [capacity, setCapacity] = useState("");

    const getData = async () => {
        return await searchTask(query)
            .then(res => {
                setTasks(res.data);
            }).catch(err => console.log(err));
    };

    useEffect(() => {
        getData();
    }, [query]);

    const handleChange = (e) => {
        setQuery(e.target.value);
    };

    const handleSearchSystem = (e) => {
        const input = e.target.value;
        setSystem(input);
        filterTasks();
    };

    const handleSearchPL = (e) => {
        const input = e.target.value;
        setProductLine(input);
        filterTasks();
    };

    const handleSearchModel = (e) => {
        const input = e.target.value;
        setModel(input);
        filterTasks();
    };

    const handleSearchType = (e) => {
        const input = e.target.value;
        setType(input);
        filterTasks();
    };

    const handleSearchCapacity = (e) => {
        const input = e.target.value;
        setCapacity(input);
        filterTasks();
    };

    const filterTasks = () => {
        let filteredTasks = [...tasks];

        if (system) {
            filteredTasks = filteredTasks.filter(task =>
                task.system && (task.system.toLowerCase() === system.toLowerCase() || task.system.toLowerCase().startsWith(system.toLowerCase()))
            );
        }
        if (productLine) {
            filteredTasks = filteredTasks.filter(task =>
                task.productline && (task.productline.toLowerCase() === productLine.toLowerCase() || task.productline.toLowerCase().includes(productLine.toLowerCase()))
            );
        }
        if (model) {
            filteredTasks = filteredTasks.filter(task =>
                task.model && (task.model.toLowerCase() === model.toLowerCase() || task.model.toLowerCase().startsWith(model.toLowerCase()))
            );
        }
        if (type) {
            filteredTasks = filteredTasks.filter(task =>
                task.type && (task.type.toLowerCase() === type.toLowerCase() || task.type.toLowerCase().startsWith(type.toLowerCase()))
            );
        }
        if (capacity) {
            const inputValue = parseFloat(capacity);
            if (!isNaN(inputValue)) {
                filteredTasks = filteredTasks.filter(task => task.capacity > inputValue);
            }
        }

        setTasks(filteredTasks);
    };

    const handleClearFilter = () => {
        setSystem("");
        setProductLine("");
        setModel("");
        setType("");
        setCapacity("");
        getData(); // Reset to original data
    };

    const row = tasks.map((task, index) => (
        <tr key={index}>
            <td>{task.system}</td>
            <td>{task.productline}</td>
            <td>{task.model}</td>
            <td>{task.type}</td>
            <td>{task.capacity}</td>
            {/* Add other task properties as needed */}
        </tr>
    ));

    return (
        <>
            <HeaderComponent />
            <Container style={{ alignItems: 'center', marginLeft: 100 }}>
                <h1 style={{ marginLeft: 300 }} className='text-center'>Panasonic Devices</h1>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={query} onChange={handleChange} />
                </Form>

                <h3>System</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={system} onChange={handleSearchSystem} />
                </Form>

                <h3>Product Line</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={productLine} onChange={handleSearchPL} />
                </Form>

                <h3>Model</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={model} onChange={handleSearchModel} />
                </Form>

                <h3>Type</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={type} onChange={handleSearchType} />
                </Form>

                <h3>Capacity</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control type='text' value={capacity} onChange={handleSearchCapacity} />
                </Form>

                <Button onClick={handleClearFilter}>Clear Filter</Button>
                <Table>
                    <thead>
                        <tr>
                            <th>System</th>
                            <th>Product Line</th>
                            <th>Model</th>
                            <th>Type</th>
                            <th>Capacity</th>
                            {/* Add other headers as needed */}
                        </tr>
                    </thead>
                    <tbody>
                        {row}
                    </tbody>
                </Table>
            </Container>
        </>
    );
};

export default EmployeeInf;
