import React, { useEffect, useState } from 'react';
import { Button, Modal, Table } from 'react-bootstrap';
import { getListTasks } from '../api/Task';

const ListTask = () => {
    const [tasks, setTasks] = useState();
    const getData = async () => {
        return await getListTasks().then(res => setTasks(res.data)).catch(err => console.log(err))
    }
    useEffect(() => {
        getData();
    },[])
    return (
        <>
            <Button>View All Tasks</Button>
            <Modal>
                <Modal.Header>List Tasks</Modal.Header>
                <Modal.Body>
                    <Table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Description</th>
                                <th>Deadline</th>
                                <th>Manager Name</th>
                            </tr>
                        </thead>
                        <tbody>

                        </tbody>
                    </Table>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default ListTask;
