import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import { getEmployeeById } from '../../api/Employee';
import { Form, Table } from 'react-bootstrap';

const EmployeeInf = () => {
    const state = useLocation().state
    // const [info, setInfo] = useState()
    // useEffect(() => {
    //     getEmployeeById(data.id).then(res => setInfo(res.data)).catch(err => console.log(err))
    // },[])
    return (
        <Form>
            <Form.Group>
                <Form.Label>FirstName</Form.Label>
                <Form.Control type='text'  value={state.firstName}/>
            </Form.Group>
            <Form.Group>
                <Form.Label>LastName</Form.Label>
                <Form.Control type='text'  value={state.lastName}/>
            </Form.Group>
            <Form.Group>
                <Form.Label>Email</Form.Label>
                <Form.Control type='email'  value={state.email}/>
            </Form.Group>
        </Form>
    );
};

export default EmployeeInf;
