import React, { useState } from 'react';
import { Button, Form, Modal } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { createTask } from '../api/Task';

const CreateTask = () => {
    const managerName = useParams();
    const [show, setShow] = useState(false);
    const handleSubmit = (e) => {
        e.preventDefault()
        // const name = e.target.name.value
        // const desc = e.target.desc.value
        // const dl = e.target.dl.value

        var device = e.target
        console.log(device)
        const task = {
           system: device.system,
           product_line: device.prl,
           model: device.model,
           type: device.type,
           capacity: device.capacity,
        }
        console.log(task)
        createTask(task)
        setShow(false)
    }
    return (
        <>
            <Button onClick={() => setShow(true)}>Create Device</Button>
            <Modal show={show} onHide={()=>setShow(false)} centered>
                <Modal.Header>Create New Device</Modal.Header>
                <Modal.Body>
                    <form onSubmit={handleSubmit}>
                        <Form.Group>
                            <Form.Label>System</Form.Label>
                            <Form.Control id="system" type = 'text'/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Product Line</Form.Label>
                            <Form.Control id="prl" type = 'text'/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Model</Form.Label>
                            <Form.Control id="model" type = 'text'/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Type</Form.Label>
                            <Form.Control id="typ" type = 'text'/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Capacity</Form.Label>
                            <Form.Control id="cap" type = 'int'/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Insert</Form.Label>
                            <Form.Control type = 'submit'/>
                        </Form.Group>
                    </form>
                    {/* <Button onClick={handleSubmit}>Save</Button> */}
                    <Button className='my-2 mx-2' onClick={() => setShow(false)}>Cancle</Button>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default CreateTask;
