import React, { useEffect, useState } from 'react';
import { Button, Container, Form, Table } from 'react-bootstrap';
import { searchTask } from '../../api/Manager';
import HeaderComponent from '../../components/Header';

const EmployeeInf = () => {
    const [originalTasks, setOriginalTasks] = useState([]); // Store original tasks
    const [tasks, setTasks] = useState([]);
    const [query, setQuery] = useState("");
    const [searchSystem, setSearchSystem] = useState("");
    const [searchPL, setSearchPL] = useState("");
    const [searchModel, setSearchModel] = useState("");
    const [searchType, setSearchType] = useState("");
    const [searchCapacity, setSearchCapacity] = useState("");

    const getData = async () => {
        return await searchTask(query)
            .then(res => {
                setOriginalTasks(res.data); // Store original tasks
                setTasks(res.data);
            }).catch(err => console.log(err));
    };

    useEffect(() => {
        getData();
    }, [query]);

    const handleChange = (e) => {
        setQuery(e.target.value);
    };

    const filterTasks = (filteredTasks) => {
        setTasks(filteredTasks);
    };

    const handleSearchSystem = (e) => {
        const input = e.target.value.toLowerCase();
        setSearchSystem(input); // Update state for the system input
        const filteredTasks = originalTasks.filter(task =>
            task.system && (task.system.toLowerCase() === input || task.system.toLowerCase().startsWith(input))
        );
        filterTasks(filteredTasks);
    };

    const handleSearchPL = (e) => {
        const input = e.target.value.toLowerCase();
        setSearchPL(input); // Update state for the product line input
        const filteredTasks = originalTasks.filter(task =>
            task.productline && (task.productline.toLowerCase() === input || task.productline.toLowerCase().includes(input))
        );
        filterTasks(filteredTasks);
    };

    const handleSearchModel = (e) => {
        const input = e.target.value.toLowerCase();
        setSearchModel(input); // Update state for the model input
        const filteredTasks = originalTasks.filter(task =>
            task.model && (task.model.toLowerCase() === input || task.model.toLowerCase().startsWith(input))
        );
        filterTasks(filteredTasks);
    };

    const handleSearchType = (e) => {
        const input = e.target.value.toLowerCase();
        setSearchType(input); // Update state for the type input
        const filteredTasks = originalTasks.filter(task =>
            task.type && (task.type.toLowerCase() === input || task.type.toLowerCase().startsWith(input))
        );
        filterTasks(filteredTasks);
    };

    const handleSearchCapacity = (e) => {
        const inputValue = parseFloat(e.target.value);
        setSearchCapacity(e.target.value); // Update state for the capacity input

        if (isNaN(inputValue)) {
            filterTasks(originalTasks); // Reset to original if input is not a number
            return;
        }

        const filtered = originalTasks.filter(task => task.capacity > inputValue);
        const closestLargerTask = filtered.reduce((closest, task) => {
            if (!closest || task.capacity < closest.capacity) {
                return task;
            }
            return closest;
        }, null);

        filterTasks(closestLargerTask ? [closestLargerTask] : []);
    };

    const handleClearFilter = () => {
        setTasks(originalTasks); // Reset to original tasks
        setQuery(""); // Clear query input
        setSearchSystem(""); // Clear system input
        setSearchPL(""); // Clear product line input
        setSearchModel(""); // Clear model input
        setSearchType(""); // Clear type input
        setSearchCapacity(""); // Clear capacity input
    };

    const row = tasks.map((task, index) => (
        <tr key={index}>
            <td>{task.system}</td>
            <td>{task.productline}</td>
            <td>{task.model}</td>
            <td>{task.type}</td>
            <td>{task.capacity}</td>
            {/* Add other task properties as needed */}
        </tr>
    ));

    return (
        <>
            <HeaderComponent />
            <Container style={{ alignItems: 'center', marginLeft: 100 }}>
                <h1 style={{ marginLeft: 300 }} className='text-center'>Panasonic Devices</h1>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={query} 
                        onChange={handleChange} 
                    />
                </Form>

                <h3>System</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchSystem} 
                        onChange={handleSearchSystem} 
                    />
                </Form>

                <h3>Product Line</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchPL} 
                        onChange={handleSearchPL} 
                    />
                </Form>

                <h3>Model</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchModel} 
                        onChange={handleSearchModel} 
                    />
                </Form>

                <h3>Type</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='text' 
                        value={searchType} 
                        onChange={handleSearchType} 
                    />
                </Form>

                <h3>Capacity</h3>
                <Form style={{ marginLeft: 200 }}>
                    <Form.Control 
                        type='number' 
                        value={searchCapacity} 
                        onChange={handleSearchCapacity} 
                    />
                </Form>

                <Button onClick={handleClearFilter}>Clear Filter</Button>
                <Table>
                    <thead>
                        <tr>
                            <th>System</th>
                            <th>Product Line</th>
                            <th>Model</th>
                            <th>Type</th>
                            <th>Capacity</th>
                            {/* Add other headers as needed */}
                        </tr>
                    </thead>
                    <tbody>
                        {row}
                    </tbody>
                </Table>
            </Container>
        </>
    );
};

export default EmployeeInf;
