import React, { useState } from 'react';
import { Button, Form, Modal } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { createTask } from '../api/Task';

const CreateTask = () => {
    const { managerName } = useParams(); // Extract manager name from URL params
    const [show, setShow] = useState(false);
    const [formData, setFormData] = useState({
        system: '',
        productline: '',
        model: '',
        type: '',
        capacity: '',
    });

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const task = {
            ...formData, // Spread the formData object to create the task
        };

        try {
            await createTask(task); // Call the API to create a task
            alert('Device created successfully!'); // Show success message
            setShow(false); // Close the modal
            setFormData({ // Reset form data
                system: '',
                productline: '',
                model: '',
                type: '',
                capacity: '',
            });
        } catch (error) {
            console.error('Error creating task:', error);
            alert('Failed to create device.'); // Show error message
        }
    };

    return (
        <>
            <Button onClick={() => setShow(true)}>Create Device</Button>
            <Modal show={show} onHide={() => setShow(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Create New Device</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group>
                            <Form.Label>System</Form.Label>
                            <Form.Control 
                                id="system" 
                                type='text' 
                                value={formData.system} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Product Line</Form.Label>
                            <Form.Control 
                                id="productline" 
                                type='text' 
                                value={formData.productline} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Model</Form.Label>
                            <Form.Control 
                                id="model" 
                                type='text' 
                                value={formData.model} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Type</Form.Label>
                            <Form.Control 
                                id="type" 
                                type='text' 
                                value={formData.type} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Capacity</Form.Label>
                            <Form.Control 
                                id="capacity" 
                                type='number' 
                                value={formData.capacity} 
                                onChange={handleInputChange} 
                                required 
                            />
                        </Form.Group>
                        <Button type='submit' className='my-2'>Insert</Button>
                    </Form>
                    <Button className='my-2 mx-2' onClick={() => setShow(false)}>Cancel</Button>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default CreateTask;
