import React, { useState } from 'react';
import { Button, Form, FormControl, FormGroup, FormLabel } from 'react-bootstrap';
import HeaderComponent from '../components/Header';
import { createEmployee } from '../api/CreateEmployee';

const CreateEmployee = () => {
    const [state, setState] = useState({
        firstName:"",
        lastName:"",
        email:""
    });
    const handleSubmit = () => {
        createEmployee(state);
    }
    return (
        <>
            <HeaderComponent />
            <h1 className="text-center">Add Employee</h1>
            <Form>
                <FormGroup>
                    <FormLabel>FirstName</FormLabel>
                    <FormControl type='text' onChange={((e) => setState({firstName:e.target.value}))} value={state.firstName}/>
                </FormGroup>
                <FormGroup>
                    <FormLabel>LastName</FormLabel>
                    <FormControl type='text' onChange={((e) => setState({lastName:e.target.value}))} value={state.lastName}/>
                </FormGroup>
                <FormGroup>
                    <FormLabel>Email</FormLabel>
                    <FormControl type='email' onChange={((e) => setState({email:e.target.value}))} value={state.email}/>
                </FormGroup>
                <Button onClick={handleSubmit}> Submit </Button>
            </Form>
        </>
    );
};

export default CreateEmployee;
