import React, { useEffect, useState } from 'react';
import { Button, Modal, Table } from 'react-bootstrap';
import { getListTasks } from '../api/Task';

const ListTask = () => {
    const [tasks, setTasks] = useState([]);
    const getData = async () => {
        return await getListTasks().then(res => setTasks(res.data)).catch(err => console.log(err))
    }
    useEffect(() => {
        getData();
    },[])
    return (
        <>
            <Table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Deadline</th>
                        <th>Manager Name</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        tasks.forEach((task) => {
                            console.log(task.name)
                        })
                        /* tasks.forEach((task) => {
