import React, { useEffect, useState } from 'react';
import { Button, Container, Form, Modal, Row, Table } from 'react-bootstrap';
import { getListTasks, getListTasksById } from '../api/Task';
import { useParams } from 'react-router-dom';
import HeaderComponent from '../components/Header';

const ListTask = () => {
    const id = useParams().id
    const [tasks, setTasks] = useState([]);
    const getData = async () => {
        return await getListTasks().then(res => setTasks(res.data)).catch(err => console.log(err))
    }
    useEffect(() => {
        getData();
    },[])
    const row = tasks.map((task,index) => (
        <tr key={index}>
            <td>{task.name}</td>
            <td>{task.description}</td>
            <td>{task.deadline}</td>
            <td>{task.managerName}</td>
        </tr>
    )) 
    return (
        <>
        <HeaderComponent />
        <Row><h1 className='text-center'>Employee List</h1></Row>
            <Container>
            <Form>
                <Form.Control type='text'></Form.Control>
                <Button>Search</Button>
            </Form>
            <Table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Deadline</th>
                        <th>Manager Name</th>
                    </tr>
                </thead>
                <tbody>
                    {row}
                </tbody>
            </Table>
            </Container>
        </>
    );
};

export default ListTask;
