import React, { useEffect, useState } from 'react';
import { deleteEmployee, getListEmployee } from '../api/Employee';
import {Button, Container, Row, Table} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import HeaderComponent from '../components/Header';
import FooterComponent from '../components/Footer';
import { Link } from 'react-router-dom';
import UpdateEmployee from './UpdateEmployee';
const ListEmployee = () => {
    const [list,setList] = useState([]);
    const loadData = async () => {
        return await getListEmployee().then((res)=>setList(res))
    }
    useEffect(()=>{
        loadData()
    },[])

    const deleteE = (id) => {
        deleteEmployee(id).then(setList((prev) => prev.filter(employee => employee.id !== id) ))
    }
    return (
        <>
            <HeaderComponent />
            <Row><h1 className='text-center'>Employee List</h1></Row>
            <Container>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            list.map((employee) => (
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.firstName}</td>
                                    <td>{employee.lastName}</td>
                                    <td>{employee.email}</td>
                                    <td>
                                        <Link to={{pathname:`../updateEmployee/${employee.id}`}}><Button>Update</Button></Link>
                                        {/* <Button onClick={deleteE(employee.id)}>Delete</Button> */}
                                    </td>
                                </tr>
                            ))
                        }
                    </tbody>
                </Table>
                <Link to={{pathname:"../createEmployee"}}> <Button>Create Employee</Button></Link>
            </Container>
        </>
    );
};

export default ListEmployee;
