import React, { useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import { Container, Form, Table } from 'react-bootstrap';
import { getEmployeeById } from '../../api/Employee';
import HeaderComponent from '../../components/Header';
import { searchTask } from '../../api/Manager';
import { getAllManager } from '../../api/Task';

const EmployeeInf = () => {
    const [tasks, setTasks] = useState([])
    const [key, setKey] = useState([])
    const [query, setQuery] = useState("");

    const getData = async () => {
        return await searchTask(query, key).then(res => setTasks(res.data)).catch(err => console.log(err))
    }
    useEffect(() => {
        getData();
    },[query])

    const handleChange = (e) => {
        setQuery(e.target.value)
    }

    useEffect(() => {
        getAllManager().then(res => setTasks(res.data)).catch(err => console.log(err))
    },[])

    const handleSearchSystem = (e) => {
        const input = e.target.value;

        const filteredTasks = tasks.filter(task =>
            task.system && task.system.toLowerCase().includes(input.toLowerCase())
        );

        setTasks(filteredTasks); // Update tasks with the filtered result
    }

    const handleSearchPL = (e) => {
        setKey("product_line")
        setQuery(e.target.value)
    }

    const handleSearchModel = (e) => {
        setKey("model")
        setQuery(e.target.value)
    }

    const handleSearchType = (e) => {
        setKey("type")
        setQuery(e.target.value)
    }

    const handleSearchCapacity = (e) => {
        setKey("capacity")
        setQuery(e.target.value)
    }

    const row = tasks.map((task,index) => (
        <tr key={index}>
            <th>{task.system}</th>
            <th>{task.productline}</th>
            <th>{task.model}</th>
            <th>{task.type}</th>
            <th>{task.capacity}</th>
            <th>{task.ratedcoolingcap}</th>
            <th>{task.dimension}</th>
            <th>{task.runingcurrent}</th>
            <th>{task.ratedpowerinput}</th>
            <th>{task.powersupply}</th>
            <th>{task.airflow}</th>
            <th>{task.extstaticpressure}</th>
            <th>{task.ratedCOP}</th>
            <th>{task.refriger}</th>
            <th>{task.noise}</th>
            <th>{task.weight}</th>
            <th>{task.liquidpipe}</th>
            <th>{task.gaspipe}</th>
            <th>{task.accessory}</th>
        </tr>
    )) 
    return (
        <>
        <HeaderComponent/>
        <Container style={{
                    alignItems: 'center',
                    marginLeft: 100,
                    }}>
            <h1 style={{
                    marginLeft: 300,
                    }} className='text-center'>Task</h1>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleChange}></Form.Control>
            </Form>

            <h3>System</h3>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleSearchSystem}></Form.Control>
            </Form>


            <h3>Product Line</h3>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleSearchPL}></Form.Control>
            </Form>

            <h3>Model</h3>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleSearchModel}></Form.Control>
            </Form>


            <h3>Type</h3>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleSearchType}></Form.Control>
            </Form>


            <h3>Capacity</h3>
            <Form style={{
                    marginLeft: 200,
                    }}>
                <Form.Control type='text' onChange={handleSearchCapacity}></Form.Control>
            </Form>
            <Table >
                <thead >
                    <tr>
                        <th>System</th>
                        <th>Product Line</th>
                        <th>Model</th>
                        <th>Type</th>
                        <th>Capacity</th>
                        <th>Rated Cooling Cap.at 19 CWB (kW)</th>
                        <th>Dimension (HxWxD) (mm)</th>
                        <th>Running Current (A)</th>
                        <th>Rated Power Input (kW)</th>
                        <th>Power Supply (V/Ph/Hz)</th>
                        <th>Air Flow (m3/h)</th>
                        <th>Ext.Static Pressure (Pa)</th>
                        <th>Rated COP</th>
                        <th>Refrigerant</th>
                        <th>Noise dB(A)</th>
                        <th>Weight (kg)</th>
                        <th>Liquid Pipe</th>
                        <th>Gas Pipe</th>
                        <th>Accessory </th>
                    </tr>
                </thead>
                <tbody>
                    {row}
                </tbody>
            </Table>
        </Container>
        </>
    );
};

export default EmployeeInf;
